INSERT INTO `adm_document_templates` (`id`, `company_id`, `branch_id`, `doc_type`, `template_html`, `created_by`, `updated_by`, `created_at`, `updated_at`) VALUES
(1, 1, 1, 'PAYSLIP', '<div style=\"display:flex;justify-content:space-between;align-items:center;\">\n  <div>{{{company.logoHtml}}}</div>\n  <div style=\"text-align:right\">\n    <div style=\"font-weight:700;\">{{company.name}}</div>\n    <div>{{company.address}}</div>\n    <div>{{company.city}} {{company.state}} {{company.country}}</div>\n    <div>{{company.phone}} . {{company.email}}</div>\n  </div>\n</div>\n<h2 style=\"margin:12px 0;\">Payslip</h2>\n<table style=\"width:100%;border-collapse:collapse;\">\n  <tr><th style=\"text-align:left;\">Period</th><td>{{payslip.period}}</td></tr>\n  <tr><th style=\"text-align:left;\">Employee</th><td>{{payslip.employee}}</td></tr>\n  <tr><th style=\"text-align:left;\">Net Pay</th><td>{{payslip.netPay}}</td></tr>\n  <tr><th style=\"text-align:left;\">Status</th><td>{{payslip.status}}</td></tr>\n</table>', NULL, NULL, '2026-01-30 07:52:08.000', '2026-01-30 07:52:08.000'),
(2, 1, 1, 'RECEIPT_VOUCHER', '<div style=\"display:flex;justify-content:space-between;align-items:center;\">\n  <div>{{{company.logoHtml}}}</div>\n  <div style=\"text-align:right\">\n    <div style=\"font-weight:700;\">{{company.name}}</div>\n    <div>{{company.addressLine1}}</div>\n    <div>{{company.addressLine2}}</div>\n    <div>{{company.phone}} . {{company.email}}</div>\n  </div>\n</div>\n<h2 style=\"margin:12px 0;\">Receipt Voucher</h2>\n<div>Receipt No: {{receipt.receiptNo}}</div>\n<div>Date: {{receipt.dateTime}}</div>\n<table style=\"width:100%;border-collapse:collapse;margin-top:8px;\">\n  <thead>\n    <tr><th style=\"text-align:left;\">Description</th><th class=\"right\">Amount</th></tr>\n  </thead>\n  <tbody>\n    {{#each items}}\n      <tr><td>{{name}}</td><td class=\"right\">{{lineTotal}}</td></tr>\n    {{/each}}\n  </tbody>\n</table>\n<div style=\"text-align:right;margin-top:8px;font-weight:700;\">Total: {{totals.total}}</div>', NULL, NULL, '2026-01-30 07:52:33.000', '2026-01-30 07:52:33.000'),
(3, 1, 1, 'PAYMENT_VOUCHER', '<div style=\"display:flex;justify-content:space-between;align-items:center;\">\n  <div>{{{company.logoHtml}}}</div>\n  <div style=\"text-align:right\">\n    <div style=\"font-weight:700;\">{{company.name}}</div>\n    <div>{{company.addressLine1}}</div>\n    <div>{{company.addressLine2}}</div>\n    <div>{{company.phone}} . {{company.email}}</div>\n  </div>\n</div>\n<h2 style=\"margin:12px 0;\">Payment Voucher</h2>\n<div>Payment No: {{payment.paymentNo}}</div>\n<div>Date: {{payment.dateTime}}</div>\n<table style=\"width:100%;border-collapse:collapse;margin-top:8px;\">\n  <thead>\n    <tr><th style=\"text-align:left;\">Description</th><th class=\"right\">Amount</th></tr>\n  </thead>\n  <tbody>\n    {{#each items}}\n      <tr><td>{{name}}</td><td class=\"right\">{{lineTotal}}</td></tr>\n    {{/each}}\n  </tbody>\n</table>\n<div style=\"text-align:right;margin-top:8px;font-weight:700;\">Total: {{totals.total}}</div>', NULL, NULL, '2026-01-30 07:52:53.000', '2026-01-30 07:52:53.000'),
(4, 1, 1, 'INVOICE', '<style>/* Global print CSS */\nbody { font-family: system-ui, -apple-system, Segoe UI, Roboto, sans-serif; margin: 0; padding: 24px; color: #0f172a; background: #fff; }\ntable { border-collapse: collapse; width: 100%; }\nth, td { border: 1px solid #e2e8f0; padding: 6px 8px; vertical-align: top; }\nth { background: #f8fafc; text-align: left; }\n.right { text-align: right; }\n.center { text-align: center; }\n.title { font-weight: 800; font-size: 20px; }\n.subtitle { font-size: 12px; color: #475569; }\n.totals td { padding: 4px 6px; }\n@media print {\n  @page { margin: 1cm; }\n  body { -webkit-print-color-adjust: exact; print-color-adjust: exact; }\n}</style><div style=\"display:flex;justify-content:space-between;gap:16px;align-items:flex-start;\"><div style=\"display:flex;align-items:center;justify-content:space-between;gap:12px;\">\n  <div style=\"flex:0 0 160px;height:80px;display:flex;align-items:center;justify-content:center;overflow:hidden;\">{{{company.logoHtml}}}</div>\n  <div style=\"text-align:right;font-size:12px;color:#475569;\">\n    <div style=\"font-weight:700;font-size:18px;color:#0f172a;\">{{company.name}}</div>\n    <div>{{company.address}}</div>\n    <div>{{company.city}} {{company.state}} {{company.country}}</div>\n    <div>{{company.phone}} {{company.email}}</div>\n    <div>{{company.website}}</div>\n  </div>\n</div><div style=\"text-align:center;margin-top:6px;\">\n  <div style=\"font-weight:800;font-size:18px;color:#296d8f;\">* Sales Invoice *</div>\n</div>\n<div style=\"display:flex;justify-content:space-between;font-size:12px;margin-top:6px;\">\n  <div></div>\n  <div style=\"text-align:right;\">\n    <div>Invoice No: {{invoice.invoice_no}}</div>\n    <div>Invoice Date: {{invoice.invoice_date}}</div>\n  </div>\n</div></div><hr /><div style=\"display:flex;justify-content:space-between;font-size:12px;margin-top:8px;\">\n  <div style=\"max-width:60%;\">\n    <div style=\"font-weight:700;margin-bottom:4px;\">Bill To</div>\n    <div>{{customer.name}}</div>\n    <div>{{customer.address}}</div>\n    <div>{{customer.city}}, {{customer.state}} {{customer.country}}</div>\n    <div>{{customer.phone}} {{customer.email}}</div>\n  </div>\n</div><table style=\"font-size:12px;\">\n  <thead>\n    <tr>\n      <th>Description</th>\n      <th style=\"text-align:right;\">Qty</th>\n      <th style=\"text-align:right;\">Unit Price</th>\n      <th style=\"text-align:right;\">Total</th>\n    </tr>\n  </thead>\n  <tbody>\n    {{#each items}}\n      <tr>\n        <td>{{item_name}}</td>\n        <td style=\"text-align:right;\">{{qty}}</td>\n        <td style=\"text-align:right;\">{{unit_price}}</td>\n        <td style=\"text-align:right;\">{{total}}</td>\n      </tr>\n    {{/each}}\n  </tbody>\n</table><div style=\"margin-top:12px;display:flex;justify-content:flex-end;\">\n  <table style=\"width:320px;font-size:12px;\">\n    <tbody>\n      <tr><td>Subtotal</td><td style=\"text-align:right;\">{{totals.subtotal}}</td></tr>\n      <tr><td>Discount</td><td style=\"text-align:right;\">{{totals.discount}}</td></tr>\n      <tr><td><strong>Total</strong></td><td style=\"text-align:right;\"><strong>{{totals.total}}</strong></td></tr>\n    </tbody>\n  </table>\n</div>\n<div style=\"margin-top:8px;font-size:12px;\"><strong>Amount in Words:</strong> ...........................................................</div><div style=\"margin-top:12px;display:flex;justify-content:space-between;font-size:12px;\">\n  <div>Prepared by: ____________________</div>\n  <div>Authorized by: ____________________</div>\n</div>', NULL, NULL, '2026-01-30 07:53:25.000', '2026-02-10 08:59:18.000'),
(5, 1, 1, 'DELIVERY_NOTE', '<h2>Delivery Note</h2><div>Delivery No: {{delivery.delivery_no}}</div><div>Date: {{delivery.delivery_date}}</div><div>Customer: {{customer.name}}</div>{{#each items}}<div>{{name}} {{qty}} {{uom}} {{total}}</div>{{/each}}<div>Total: {{totals.total}}</div>', NULL, NULL, '2026-01-30 07:59:09.000', '2026-01-30 07:59:09.000');
